export class BaseUtil {
  static deepClone(originalObject) {
    return JSON.parse(JSON.stringify(originalObject));
  }
  static flattenArray(arr) {
    let result = [];
    arr.forEach(item => {
      if (Array.isArray(item)) {
        result = result.concat(BaseUtil.flattenArray(item));
      } else {
        result.push(item);
      }
    });
    return result;
  }
}
