import { DataSetsComponent } from './components/data-sets/data-sets.component';
///<reference types="chrome"/>
// be aware, this weird shit must be first to work in extension mode

// angular imports
import { Component, ViewChild } from "@angular/core";
import { OnInit } from '@angular/core';

// my imports
import { BaseComponent } from "../_base/components/_base/base.component";
import { SettingDecorator } from './../_base/decorators/setting/setting.decorator';


@Component({
  selector: 'app-options',
  templateUrl: './options.component.html',
  styleUrls: [
    './options.component.scss'
  ],
})
export class OptionsComponent extends BaseComponent implements OnInit {

  @ViewChild('enviroments')
  private dataSetsComponent !: DataSetsComponent;

  public exitButtonState : boolean = false; // false == can save

  ngOnInit(): void {
    this.handleExitButtonBehaviourDuringSaveOperations();
  }


  public saveAndExit() : void {
    chrome.tabs.getCurrent(function(tab : any) {
        chrome.tabs.remove(tab.id, function() { });
    });
  }

  public addEnviroment() : void {
    this.dataSetsComponent.enviroments?.push();
  }


  private handleExitButtonBehaviourDuringSaveOperations() {
    SettingDecorator.getSaveEventEmmiter().subscribe((state : boolean) => {
      this.exitButtonState = state;
    });
  }

}
<main>

    <section>
        <h1>Nastavenia</h1>
    </section>

    <section>

        <h2>Správanie</h2>

        <article class="componentElementFix">
            <component-auto-submit-resource-id></component-auto-submit-resource-id>
        </article>

        <article class="componentElementFix">
            <component-check-on-all-enviroments></component-check-on-all-enviroments>
        </article>

        <article>
            <label for="enviromentId">Aktívne prostredie:</label>
            <component-select-enviroment></component-select-enviroment>
        </article>

    </section>

    <section id="dataSources">

        <h2>Dátové zostavy</h2>
        <i>Oddeľujte jednotlivé URL na dátové zostavy novým riadkom</i>

        <component-data-sets #enviroments></component-data-sets>

        <button (click)="addEnviroment()">Pridať ďalšie prostredie</button>

    </section>

    <section class="right">
        <button (click)="saveAndExit()"
                [class]="exitButtonState ? 'disabled' : ''">
            {{ exitButtonState ? 'Ukladám...' : 'Zavrieť' }}
        </button>
    </section>

</main>
