import { EventEmitter } from '@angular/core';
export class EventsUtil {
  static notifySettingsSaved(result) {
    this.SETTINGS_SAVED_EMMITER.emit(result);
  }
  static getSettingsSavedEmiter() {
    // true emitted - settings saving sequence start
    // false emitted - settings saved
    return this.SETTINGS_SAVED_EMMITER;
  }
  static notifySettingsEnviromentsChanged(result) {
    this.SETTINGS_ENVIROMENTS_CHANGED.emit(this.convertDataSetsNgModelType(result));
  }
  static getSettingsEnviromentChangedEmiter() {
    return this.SETTINGS_ENVIROMENTS_CHANGED;
  }
  static convertDataSetsNgModelType(content) {
    return content === undefined ? undefined : content?.map(x => ({
      id: x.id,
      name: x.name
    }));
  }
}
EventsUtil.SETTINGS_SAVED_EMMITER = new EventEmitter();
EventsUtil.SETTINGS_ENVIROMENTS_CHANGED = new EventEmitter();
