import { SettingDecoratorConverter } from 'src/app/_base/decorators/setting.decorator';

interface DataSetsNgModelRecordFormat {
    id : string;
    name : string;
    datasets : string;
}

type DataSetsNgModelType = DataSetsNgModelRecordFormat[];

interface DataSetsStoreRecordFormat {
    id : string;
    name : string;
    datasets : string[];
}

type DataSetsStoreType = DataSetsStoreRecordFormat[] | undefined;

export class DataSetsSettingDecoratorConverter 
    implements SettingDecoratorConverter<DataSetsNgModelType, DataSetsStoreType> 
{
    modelConversion(content : DataSetsNgModelType) : DataSetsStoreType {
        //TODO shouldnt I be able DRY this by some custom method decorator (annotation) ?
        if (content === undefined) {
            return undefined;
        }
        return content
            .map((x) => ({
                id: x.id,
                name: x.name,
                datasets: x.datasets.trim().split('\n').map((x) => x.trim())
            }));
    }

    storeConversion(content : DataSetsStoreType) : DataSetsNgModelType {
        if (content === undefined) {
            return undefined;
        }
        return content
            .map((x) => ({
                id: x.id,
                name: x.name,
                datasets: x.datasets.join('\n')
            }));
    }
}
