import { BaseUtil } from './../utils/base.util';
import { Injectable } from "@angular/core";
import { EnviromentUtil, SettingsNames } from 'src/app/_base/utils/enviroment.util';
import { QueryService, Settings, KeyValuePair } from './query.service';
import { StoreService } from './store.service';

@Injectable({
    providedIn : 'root'
})
export class QueryServiceImpl implements QueryService {

    private storeService : StoreService;

    constructor() {
        //FIXME dependency injection on various enviroments
        this.storeService = EnviromentUtil.getStoreService();
    }


    public open(resourceId : string | undefined) : Promise<boolean> {
        var context = this;
        return new Promise((resolve, reject) => {
            if (resourceId === undefined || resourceId === null || resourceId.trim().length < 1) {
                reject(false);
            } else {
                context.check(resourceId)
            }
        });
    }


    private check(resourceId : string) : Promise<any> {
        var context = this;
        // return new Promise((resolve, reject) => {

        // });
        return Promise.all([
            context.loadAllSettings()
        ])
        .then((settings : Settings) => {
            console.log(settings);
            context.fireRequests(resourceId, context.getRequestsToFire(settings))
            //console.log('all settings resolved promises loaded');
            //console.log(result);

        });
    }

    private fireRequests(resourceId : string, requests : string[]) : Promise<any> {
        return new Promise((resolve, reject) => { 

        });
    }

    private getRequestsToFire(settings : Settings) : string[] {
        // console.log(settings);
        // console.log(BaseUtil.flattenArray(settings).find((setting) => Object.keys(setting)[0] === SettingsNames.ENVIROMENTS));

        console.log(
        BaseUtil.flattenArray(settings)
            .find((setting) => Object.keys(setting)[0] === SettingsNames.ENVIROMENTS)
            );

        return [];   
        //let requests : string[] = this.extractSetting(settings, SettingsNames.ENVIROMENTS)
        //let requests : string[] = settings.find((setting) => setting)

    }

    private loadAllSettings() : Promise<Settings> {
        return new Promise((resolve, reject) => {

            let settingsPromises : Promise<KeyValuePair>[] = [];
            Object.values(SettingsNames).forEach((settingName : string) => {
                settingsPromises.push(this.storeService.loadWithKey(settingName));
            })

            Promise.allSettled(settingsPromises).then((results) => {
                // console.log('all settings promises settled');
                //console.log(results);
                // let settings : any = results
                //     .filter((result) => result.status === 'fulfilled')
                //     .map((result) => result.value);
                let settings : Settings = []; 
                results.forEach((result) => {
                    // console.log(result.status);
                    if (result.status === 'fulfilled') {
                        // console.log(result.value);
                        console.log(result.value);
                        settings.push(result.value);
                    }
                });

                console.log(settings);

                resolve(settings);
            }).catch(() => {
                reject('Error during loading settings from store - promises settling');
            })

        });
    }

    // private extractSetting(settings : any, settingType : SettingsNames) : any {
    //     return settings.find((setting) => Object.keys(setting).includes(settingType))
    // }

}
